import React from 'react';

const About = () => {
    return (
        <div className="container mx-auto my-12 px-4 md:px-8">
            <h1 className="text-3xl font-bold mb-4">About Us</h1>
            <p className="mb-6">
                Welcome to our Coffee Gallery! We are a small team of coffee enthusiasts who love to share our passion for coffee with the world. Our goal is to provide high-quality, delicious coffee beans from all over the world and make them accessible to everyone.
            </p>
            <p className="mb-6">
                We believe that coffee is more than just a drink; it’s a way of life. That’s why we’re dedicated to bringing you the best coffee experience possible, from the beans we source to the brewing techniques we recommend.
            </p>
            <p className="mb-6">
                Thank you for visiting our site, and we hope you enjoy our selection of coffee beans. If you have any questions or feedback, please don’t hesitate to get in touch with us.
            </p>
            <p className="font-bold">
                The Coffee Gallery Team
            </p>
        </div>
    );
};

export default About;
